import React,{useState,useEffect} from 'react';
import axios from 'axios';

const Volunteer=()=>{
    const [talent,setTalent]=useState([]);
    useEffect(()=>{
        const res=async()=>{
            const {data}=await axios.get("http://127.0.0.1:8000/api/vt/")
            console.log(data);
            setTalent(data);
        }
        res();
    },[])
console.log(talent)

const disp=talent.map((x)=>{
    console.log(x.file)

    const arr=x.file.split(".")
        arr.reverse();

    console.log(arr)
    return (
        <div style={{display:'flex',justifyContent:'center'}}>
        {arr[0]=='mp4'?<div style={{display:'block'}}> <video width="250" height="200" controls >
      <source src={x.file} />
     </video></div>:<img src={x.file} />}
     {x.image1==null?null:<img src={x.image1} alt="bufvn" />}
        {x.image2==null?null:<img src={x.image2} alt="vbfj" />}

        </div>
    )
})
    return (
        <div><h1>{disp}</h1></div>
    )
}

export default Volunteer;
