import React,{useState,useEffect} from 'react';
import axios from 'axios';
import {BsArrowRightCircleFill,BsArrowLeftCircleFill} from 'react-icons/bs';

const Slide=(props)=>{
const [image,setImage]=useState([]);
const [x,setX]=useState(0)
useEffect(()=>{
    setX(0)
    const res=async()=>{
        const {data}=await axios.get(`http://127.0.0.1:8000/api/${props.api}/`);
        console.log(data)
        setImage(data)
    };
    res()
},[])
// console.log(border1)


useEffect(() => {
    setTimeout(handlerRight, 10000);

    // return ( setTimeout(handlerRight, 2000))

}, [x,image])
// console.log(x)
 const handlerRight=()=>{

     if(x<image.length-1){
         setX(x+1)
     }
     if(x>=image.length-1){
         setX(0)
     }
 }
const handlerLeft=()=>{
    console.log(x)

    if(x<=0){
setX(image.length-1)
    }
    if(x<=image.length-1 && x>0){
        setX(x-1)
    }
    // if(x<0){
    //     setX(image.length-2)
    // }
}

console.log(props.width1)

    return (
        <div style={{position:'relative',border:`${props.border1}`}}>
            {/* <button onClick={setX(x+1)}>n k</button> */}
            <div>
                <p  className="slid"onClick={handlerLeft} style={{left:"80px"}}><BsArrowLeftCircleFill style={{color:'blue',width:'4em',size:'45px'}}/></p>
            {image.length>0?
            
            <img src={image[x].image}  style={{width:`${props.width1}`,height:'550px'}}alt="bbj" />
            :null}
                <p  className="slide1"onClick={handlerRight} style={{right:'80px'}}><BsArrowRightCircleFill/></p>
        </div>
        
        </div>
    )
}


export default Slide;
