		<tr>
		<th style={{border:'2px solid black', color:'red', fontSize:'20px', padding:'10px 20px 10px 20px', fontFamily:'font-family: Arial,Helvetica,sans-serif'}}>DATE</th>
		<th style={{border:'2px solid black', color:'red', fontSize:'20px', padding:'10px 20px 10px 20px', fontFamily:'font-family: Arial,Helvetica,sans-serif'}}>DONATED TO</th>
		<th style={{border:'2px solid black', color:'red', fontSize:'20px', padding:'10px 20px 10px 20px', fontFamily:'font-family: Arial,Helvetica,sans-serif'}}>NO OF UNITS</th>
        <th style={{border:'2px solid black', color:'red', fontSize:'20px', padding:'10px 20px 10px 20px', fontFamily:'font-family: Arial,Helvetica,sans-serif'}}>PHOTO</th>
		</tr>
